import React, {useState} from 'react'


export default function TextForm(props) {

    const [Text, setText] = useState("");
    const handleUpClick = () => {
        let newText = Text.toUpperCase();
        setText(newText)
    }

    const handleLoClick = () => {
        let newText = Text.toLowerCase();
        setText(newText)
    }
    const handleClearClick = () => {
        let newText = ""
        setText(newText)
    }

    const handleOnChange = (event) => {
        setText(event.target.value)
    }

    const handleReverseClick = () => {
        let newText = Text.split('').reverse().join('');
        setText(newText);
    }

    const handleFindReplaceClick = (findText, replaceText) => {
    let newText = Text.replace(new RegExp(findText, 'g'), replaceText);
    setText(newText);
}


    return (
        <>
        <div className="container"style={{color : props.mode ==='dark'?'white':'black'}}>
        <h1>{props.Heading}</h1>
        <div className="mb-3">

            <textarea className="form-control" id="mybox"  value={Text}  onChange={handleOnChange} style={{backgroundColor : props.mode ==='dark'?'grey':'white'}} rows="9"></textarea>
            <button className="btn btn-primary my-3" onClick={handleUpClick}>convert to Uppercase</button>
            <button className="btn btn-primary mx-2" onClick={handleLoClick}>convert to Lowercase</button>
            <button className="btn btn-primary mx-2" onClick={handleClearClick}>Clear Text</button>
            <button className="btn btn-primary mx-2" onClick={handleReverseClick}>reverse Text</button>
            <button className="btn btn-primary mx-2" onClick={handleFindReplaceClick}>find and replace Text</button>
        </div>
        </div>

        <div className="container" style={{color : props.mode ==='dark'?'white':'black'}}>
            <h1>Text Summary</h1>
            <p>{Text.split(" ").length} words and {Text.length} characters</p>
            <p> {0.008 * Text.split(" ").length} Mininutes Read</p>
            <h2>Priview</h2>
            <p>{Text}</p>
        </div>
        </>
    )
}
